import React, { Component } from "react";
import BroodjeMenu from "./BroodjeMenu";

class Broodje extends Component {
  state = {
    broodSoorten: [
      { soort: "Witte Bol", id: 0 },
      { soort: "Bruine Bol", id: 1 },
      { soort: "Witte Boterham", id: 2 },
      { soort: "Bruine Boterham", id: 3 },
      { soort: "Krentenbol", id: 4 },
      { soort: "Zuurdesem", id: 5 },
      { soort: "Geen", id: 6 }
    ],
    boter: ["Ja", "Nee"],
    beleg: ["Kaas", "Gebraden gehakt", "Ham", "Jam", "Chocopasta", "Niets"]
  };

  render() {
    return (
      <div className="container">
        <h3>Broodje </h3>
        <BroodjeMenu onderdeel={"soort"} menuOptions={this.state.boter} />
        <BroodjeMenu onderdeel={"beleg"} menuOptions={this.state.beleg} />
        <BroodjeMenu
          onderdeel={"boter"}
          menuOptions={this.state.broodSoorten}
        />
      </div>
    );
  }
}

export default Broodje;
