import React, { Component } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css/dist/js/materialize.min.js";

class BroodjeMenu extends Component {
  state = {
    broodSoorten: [
      "Witte Bol",
      "Bruine Bol",
      "Witte Boterham",
      "Bruine Boterham",
      "Krentenbol",
      "Zuurdesem",
      "Geen"
    ],
    boter: ["Ja", "Nee"],
    beleg: ["Kaas", "Gebraden gehakt", "Ham", "Jam", "Chocopasta", "Niets"]
  };

  componentDidMount() {
    M.AutoInit();
  }

  handleChange = e => {
    console.log(e);
  };

  render() {
    return (
      <div className="container">
        <div className="input-field col s12">
          <select value="1" selected onChange={handleChange}>
            <option value="1" disabled>
              Kies uw
            </option>
            {this.props.map(choice => (
              <option key={choice} value={choice.value}>
                {choice}
              </option>
            ))}
          </select>
        </div>
      </div>
    );
  }
}

export default BroodjeMenu;
