import React, { Component } from "react";
import BroodjeMenu from "./BroodjeMenu";

class Broodje extends Component {
  state = {
    broodSoorten: [
      "Witte Bol",
      "Bruine Bol",
      "Witte Boterham",
      "Bruine Boterham",
      "Krentenbol",
      "Zuurdesem",
      "Geen"
    ],
    boter: ["Ja", "Nee"],
    beleg: ["Kaas", "Gebraden gehakt", "Ham", "Jam", "Chocopasta", "Niets"]
  };

  render() {
    return (
      <div className="container">
        <h3>Broodje </h3>
        <BroodjeMenu menuOptions={this.state.broodSoorten} />
        <BroodjeMenu menuOptions={this.state.boter} />
        <BroodjeMenu menuOptions={this.state.beleg} />
      </div>
    );
  }
}

export default Broodje;
