import React, { Component } from "react";
import BroodjeMenu from "./BroodjeMenu";

class Broodje extends Component {
  state = {
    broodSoorten: [
      "Witte Bol",
      "Bruine Bol",
      "Witte Boterham",
      "Bruine Boterham",
      "Krentenbol",
      "Zuurdesem",
      "Geen"
    ],
    boter: ["Ja", "Nee"],
    beleg: ["Kaas", "Gebraden gehakt", "Ham", "Jam", "Chocopasta", "Niets"]
  };

  render() {
    return (
      <div className="container">
        <h3>Broodje </h3>
        <BroodjeMenu broodje={this.state} />
        <div>
          <select value="1">
            {this.state.broodSoorten.map(soort => (
              <option key={soort.value} value={soort.value}>
                {soort}
              </option>
            ))}
          </select>
          <select value="1">
            {this.state.boter.map(soort => (
              <option key={soort.value} value={soort.value}>
                {soort}
              </option>
            ))}
          </select>
          <select value="1">
            {this.state.beleg.map(soort => (
              <option key={soort.value} value={soort.value}>
                {soort}
              </option>
            ))}
          </select>
        </div>
      </div>
    );
  }
}

export default Broodje;
