import React, { Component } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css/dist/js/materialize.min.js";

class BroodjeMenu extends Component {
  state = {
    soort: null,
    boter: null,
    beleg: null
  };

  componentDidMount() {
    M.AutoInit();
  }
  handleChange = e => {
    this.setState({
      [e.target.id]: e.target.value
    });
    e.preventDefault();
    this.props.addOnderdeel(this.state);
  };
  render() {
    // console.log(this.state);
    // console.log(this.props.menuOptions);

    return (
      <div>
        <form>
          <div className="input-field col s12">
            <select id={this.props.onderdeel} onChange={this.handleChange}>
              <option value="" disabled selected>
                Kies uw {this.props.onderdeel}
              </option>
              {this.props.menuOptions.map((element, index) => (
                <option key={index}>{element.soort}</option>
              ))}
            </select>
          </div>
        </form>
      </div>
    );
  }
}

export default BroodjeMenu;
