import React, { Component } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css/dist/js/materialize.min.js";

class Broodje extends Component {
  state = {
    broodSoort: [
      "Witte Bol",
      "Bruine Bol",
      "Witte Boterham",
      "Bruine Boterham",
      "Krentenbol",
      "Zuurdesem",
      "Geen"
    ],
    boter: ["Ja", "Nee"],
    beleg: ["Kaas", "Gebraden gehakt", "Ham", "Jam", "Chocopasta", "Niets"]
  };
  componentDidMount() {
    M.AutoInit();
  }
  render() {
    const { broodjesList } = this.props;
    console.log(broodjesList);
    //const { broodSoortList} = broodjesList.
    return (
      <div className="container">
        <h3>Broodje 1</h3>
        <div className="input-field col s12">
          <select defaultValue="">
            <option value="" disabled>
              Broodsoort
            </option>
            <option value="1">Witte Bol</option>
            <option value="2">Bruine Bol</option>
            <option value="3">Witte Boterham</option>
            <option value="4">Bruine Boterham</option>
            <option value="5">Krentenbol</option>
            <option value="6">Zuurdesem</option>
            <option value="6">Geen</option>
          </select>
        </div>
        <div className="input-field col s12">
          <select defaultValue="">
            <option value="" disabled>
              Boter
            </option>
            <option value="1">Ja</option>
            <option value="2">Nee</option>
          </select>
        </div>
        <div className="input-field col s12">
          <select defaultValue="">
            <option value="" disabled>
              Beleg
            </option>
            <option value="1">Kaas</option>
            <option value="2">Gebraden Gehakt</option>
            <option value="3">Ham</option>
            <option value="4">Jam</option>
            <option value="5">Chocopasta</option>
            <option value="6">Niets</option>
          </select>
        </div>
      </div>
    );
  }
}

export default Broodje;
