import React, { Component } from "react";
import BroodjeMenu from "./BroodjeMenu";

class Broodje extends Component {
  state = {
    broodSoorten: [
      { soort: "Witte Bol", id: 0 },
      { soort: "Bruine Bol", id: 1 },
      { soort: "Witte Boterham", id: 2 },
      { soort: "Bruine Boterham", id: 3 },
      { soort: "Krentenbol", id: 4 },
      { soort: "Zuurdesem", id: 5 },
      { soort: "Geen", id: 6 }
    ],
    boter: [{ soort: "Ja", id: 0 }, { soort: "Nee", id: 1 }],
    beleg: [
      { soort: "Kaas", id: 0 },
      { soort: "Gebraden gehakt", id: 1 },
      { soort: "Ham", id: 2 },
      { soort: "Jam", id: 3 },
      { soort: "Chocopasta", id: 4 },
      { soort: "Niets", id: 5 }
    ]
  };

  render() {
    return (
      <div className="container">
        <h3>Broodje </h3>
        <BroodjeMenu key="1" menuOptions={this.state.broodSoorten} />
        <BroodjeMenu key="2" menuOptions={this.state.boter} />
        <BroodjeMenu key="3" menuOptions={this.state.beleg} />
      </div>
    );
  }
}

export default Broodje;
