import React, { Component } from "react";
import "materialize-css/dist/css/materialize.min.css";
import M from "materialize-css/dist/js/materialize.min.js";

class BroodjeMenu extends Component {
  state = {
    soort: null,
    boter: null,
    beleg: null
  };

  componentDidMount() {
    M.AutoInit();
  }
  handleChange = e => {
    console.log(e.target);
    this.setState({
      [e.target.id]: e.target.value
    });
  };
  render() {
    console.log(this.state);
    return (
      <div>
        <form>
          <input type="text" id="soort" onChange={this.handleChange} />
          <label htmlFor="boter">Boter:</label>
          <input type="text" id="boter" onChange={this.handleChange} />
          <label htmlFor="beleg">Beleg:</label>
          <input type="text" id="beleg" onChange={this.handleChange} />
        </form>
      </div>
    );
  }
}

export default BroodjeMenu;
